// Animación de contadores en el hero
function animateCounters() {
  const counters = document.querySelectorAll('.stat-number');
  const speed = 200; // Velocidad de la animación
  
  counters.forEach(counter => {
    const target = +counter.getAttribute('data-count');
    const count = +counter.innerText;
    const increment = target / speed;
    
    if (count < target) {
      counter.innerText = Math.ceil(count + increment);
      setTimeout(animateCounters, 1);
    } else {
      counter.innerText = target;
      counter.classList.add('animated');
    }
  });
}

// Activar cuando la sección sea visible
window.addEventListener('scroll', function() {
  const hero = document.querySelector('.hero');
  const heroPosition = hero.getBoundingClientRect().top;
  const screenPosition = window.innerHeight / 1.3;
  
  if (heroPosition < screenPosition) {
    animateCounters();
  }
});

document.addEventListener('DOMContentLoaded', () => {
  const menuToggle = document.querySelector('.menu-toggle');
  const navbar = document.querySelector('.navbar');
  const dropdown = document.querySelector('.dropdown');
  const dropdownLink = dropdown?.querySelector('a');

  // Manejar el botón de menú principal
  if (menuToggle && navbar) {
    menuToggle.addEventListener('click', () => {
      menuToggle.classList.toggle('active');
      navbar.classList.toggle('active');
      // Cerrar el submenú al abrir/cerrar el menú principal
      dropdown?.classList.remove('active');
    });
  }

  // Manejar el submenú desplegable
  if (dropdown && dropdownLink) {
    dropdownLink.addEventListener('click', (e) => {
      e.preventDefault(); // Evita la navegación inmediata
      dropdown.classList.toggle('active');
    });

    // Cerrar el submenú al hacer clic en un enlace del submenú
    const dropdownLinks = document.querySelectorAll('.dropdown-content a');
    dropdownLinks.forEach(link => {
      link.addEventListener('click', () => {
        dropdown.classList.remove('active');
        if (menuToggle && navbar) {
          menuToggle.classList.remove('active');
          navbar.classList.remove('active');
        }
      });
    });
  }
});